#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\zwj\Desktop\mini-markdown-resin\node_modules\.pnpm\rspress@1.45.6_webpack@5.102.1\node_modules\rspress\bin\node_modules;C:\Users\zwj\Desktop\mini-markdown-resin\node_modules\.pnpm\rspress@1.45.6_webpack@5.102.1\node_modules\rspress\node_modules;C:\Users\zwj\Desktop\mini-markdown-resin\node_modules\.pnpm\rspress@1.45.6_webpack@5.102.1\node_modules;C:\Users\zwj\Desktop\mini-markdown-resin\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/proc/cygdrive/c/Users/zwj/Desktop/mini-markdown-resin/node_modules/.pnpm/rspress@1.45.6_webpack@5.102.1/node_modules/rspress/bin/node_modules:/proc/cygdrive/c/Users/zwj/Desktop/mini-markdown-resin/node_modules/.pnpm/rspress@1.45.6_webpack@5.102.1/node_modules/rspress/node_modules:/proc/cygdrive/c/Users/zwj/Desktop/mini-markdown-resin/node_modules/.pnpm/rspress@1.45.6_webpack@5.102.1/node_modules:/proc/cygdrive/c/Users/zwj/Desktop/mini-markdown-resin/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../rspress/bin/rspress.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../rspress/bin/rspress.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../rspress/bin/rspress.js" $args
  } else {
    & "node$exe"  "$basedir/../rspress/bin/rspress.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
